import React, { useEffect } from "react";
import styled from "styled-components";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import { useCookies, Cookies } from "react-cookie";
import { useHistory } from "react-router";

// Components
import Home from "./pages/Home";
import Client from "./client/Client";

const StyledMenu = styled.ul`
  position: fixed;
  top: 0;
  right: 0;
  list-style: none;
  display: flex;
  flex-direction: column;
  width: 100%;
  height: 100%;
  padding: 0 1em;
  align-items: center;
  justify-content: center;
  transition: all 300ms ease;
  transform: ${({ open }) => (open ? "translateX(0)" : "translateX(100%)")};
  opacity: ${({ open }) => (open ? "1" : "0")};

  background-color: rgb(20, 26, 31);
  z-index: 9;
  }

  li {
      padding: 2em 0
  }
`;

const StyledLink = styled(Link)`
  color: white;
  text-decoration: none;
  font-size: 4rem;
  transition: all 200ms linear;

  &:hover {
    color: burlywood;
  }

  @media screen and (max-width: 600px) {
    font-size: 3rem;
  }
`;

const Menu = ({ open, toggle }) => {
  return (
    <Router>
      <StyledMenu open={open}>
        <li>
          <StyledLink to="/home" onClick={toggle}>
            Home
          </StyledLink>
        </li>
        <li>
          <StyledLink to="/clients" onClick={toggle}>
            Clients
          </StyledLink>
        </li>
      </StyledMenu>

      <Switch>
        <Route path="/clients">
          <Client />
        </Route>
        <Route path="/home">
          <Home />
        </Route>
      </Switch>
    </Router>
  );
};

const StyledBurger = styled.button`
  position: fixed;
  top: 5%;
  right: 3rem;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  width: 2rem;
  height: 2rem;
  background: transparent;
  border: none;
  cursor: pointer;
  padding: 0;
  z-index: 10;

  &:focus {
    outline: none;
  }

  &:hover {
    div:first-child,
    div:nth-child(3) {
      transform: ${({ open }) => (open ? "" : "scaleX(1)")};
    }
  }

  div {
    width: 2rem;
    height: 0.25rem;
    background: ${({ open }) => (open ? "white" : "white")};
    transition: all 300ms linear;
    position: relative;
    transform-origin: 1px;

    :first-child {
      transform: ${({ open }) =>
        open ? "rotate(45deg)" : "rotate(0) scaleX(0.5)"};
    }

    :nth-child(2) {
      opacity: ${({ open }) => (open ? "0" : "1")};
    }

    :nth-child(3) {
      transform: ${({ open }) =>
        open ? "rotate(-45deg)" : "rotate(0) scaleX(0.7)"};
    }
  }
`;

const Burger = ({ open, toggle }) => {
  return (
    <StyledBurger open={open} onClick={toggle}>
      <div />
      <div />
      <div />
    </StyledBurger>
  );
};

const Nav = () => {
  const [open, setOpen] = React.useState(false);

  const toggle = () => {
    setOpen(!open);
  };

  return (
    <div>
      <Burger open={open} toggle={toggle} />
      <Menu open={open} toggle={toggle} />
    </div>
  );
};

export default Nav;
